import React, {Component} from 'react';
import axios from 'axios';
class Home extends Component{
    state = {
        posts: []
    }
    componentDidMount () {
        axios.get('https://jsonplaceholder.typicode.com/posts') // .then method runs when the promise returned by the asynchronous axios call is resolved 
        .then(res => {
            console.log(res);
            this.setState({
                posts: res.posts.slice(0,10)
            })
        })
    }

    render() {
        const {posts} = this.state;
        const postList = posts.length ? (
            posts.map(post => {
              return (
                <div className="post card" key={post.id}>
                  <div className="card-content">
                    <span className="card-title">{post.title}</span>
                    <p>{post.body}</p>
                  </div>
                </div>
              )
            })
          ) : (
            <div className="center">No posts to show</div>
          );
        return (
            <div className="container">
                <h4 className="center">Home</h4>
                <p> Lorem ipsum dolor sit, amet consectetur adipisicing elit. Consectetur repellat, corrupti tenetur voluptates tempora amet sunt, voluptatem ullam quos ducimus cum, mollitia quae neque atque soluta dicta laborum! Velit, qui!</p>
                {postList}
            </div>
        )
    }
}
export default Home;
